                        label = "display name"
                        required
                    > 
                    </FormInput>

                    <FormInput
                        type = "email"
                        name = "email"
                        value = {email}
                        onChange = {handleChange}
                        label = "Email"
                        required
                    > 
                    </FormInput>

                    <FormInput
                        type = "password"
                        name = "password"
                        value = {password}
                        onChange = {handleChange}
                        label = "password"
                        required
                    > 
                    </FormInput>

                    <FormInput
                        type = "password"
                        name = "confirmPassword"
                        value = {confirmPassword}
                        onChange = {handleChange}
                        label = "Confirm Password"
                        required
                    > 
                    </FormInput>

                    <CustomButton type = "submit"> SIGN UP </CustomButton>
                </form>
               
        </div>
    )


    
    
}
    

export default SignUp;
