import React from "react";
import "./ToBookItem.css";
import { useState } from "react";
//items of every services like type of rooms or tours
const ToBookItem = ({id,reservationInput, name, price, imageUrl}) =>{
    const [enteredService, setEnteredService] = useState('');

    const serviceChangeHandler = (event) => {
        console.log(event.target.value);
        setEnteredService({enteredService: event.target.value});
         reservationInput({
           ...reservationInput,
           enteredService: event.target.value,
         });
         console.log({reservationInput} );
    };

    return(
        <div className="toBook-item">
            <div 
                className = "image"
                style = {{
                    backgroundImage: `url(${imageUrl})`
                }}
            />  
            <input type="checkbox" onChange={serviceChangeHandler}></input>
            <div className="toBook-footer">
                <span className="name"> {name} </span>
                <span className="price"> {price} </span>
    
            </div>
        </div>       
    )

};


export default ToBookItem;
